INSERT INTO wt_questions_candidate (qc_code ,qc_student ,qe_answer ,qe_code ,qe_score ,qc_date) VALUES (null ,#{studentName} ,#{qeAnswer} ,#{qeCode} ,(SELECT S.qe_score FROM ( SELECT qe_score ,1 AS ORDERS FROM wt_questions_enrollment where qe_code=#{qeCode} AND qe_answer=#{qeAnswer}
