mapper.insert:

INSERT INTO wt_open_lecture
      ( lec_open_code
      , lec_open_sche_code
      , mr_id
      , lec_target
      , lec_plan
      , lec_etc
      , open_lec_reg_date)
VALUES(
      CONCAT('work_num',
      (SELECT 
      		LPAD(COUNT(*)+1,5,'0')
    		FROM
    			wt_open_lecture as wt))
      , #{lecName}
      , #{mrName}
      , #{lecTarget}
      , #{lecPlan}
      , #{lecEtc}
      , NOW());

mapper.update:
UPDATE 
	wonione.wt_open_lecture
SET 
	lec_open_sche_code= #{lecOpenScheCode}
	, mr_id= #{mrId}
	, lec_target= #{lecTarget}
	, lec_plan= #{lecPlan}
	, lec_etc= #{lecEtc}
	, open_lec_reg_date= NOW()
WHERE 
	lec_open_code= #{lecOpenCode};
