mapper.insert:

INSERT INTO wt_open_lecture
      ( lec_open_code
      , lec_open_sche_code
      , mr_id
      , lec_target
      , lec_plan
      , lec_etc
      , open_lec_reg_date)
VALUES(
      CONCAT('work_num',
      (SELECT 
      		LPAD(COUNT(*)+1,5,'0')
    		FROM
    			wt_open_lecture as wt))
      , #{lecOpenScheCode}
      , #{mrId}
      , #{lecTarget}
      , #{lecPlan}
      , #{lecEtc}
      , NOW());
